mapper.update:
UPDATE nol_ticket_date
SET quantity = quantity - #{quantity}
WHERE tno = #{tno}
  AND tdate = TO_DATE(#{tdate}, 'YYYY-MM-DD')

mapper.insert:
INSERT INTO nol_reserve (rno, tdno, id, quantity)
VALUES (
  nol_reserve_seq.NEXTVAL,
  (SELECT tdno FROM nol_ticket_date
   WHERE tno = #{tno}
     AND tdate = TO_DATE(#{tdate}, 'YYYY-MM-DD')),
  #{id},
  #{quantity}
)
