mapper.select:
SELECT quantity
FROM nol_ticket_date
WHERE tno = #{tno}
AND tdate = #{tdate}

mapper.update:
UPDATE nol_ticket_date
SET quantity = quantity - #{quantity}
WHERE tno = #{tno}
AND tdate = #{tdate}

mapper.insert:
INSERT INTO nol_reserve (rno, tdno, id, quantity, discount_price)
VALUES (
nol_reserve_seq.NEXTVAL,
(SELECT tdno FROM nol_ticket_date
WHERE tno = #{tno}
AND tdate = #{tdate}),
#{id},
#{quantity},
#{totalPrice}
)

mapper.select:
SELECT r.rno, m.name AS memberName, t.name AS ticketName, td.tdate AS tdate, r.quantity, (r.quantity * t.price) AS totalPrice FROM nol_reserve r JOIN nol_ticket_date td ON r.tdno = td.tdno JOIN nol_ticket t ON td.tno = t.tno JOIN nol_member m ON r.id = m.id
AND r.id = #{id}
ORDER BY td.tdate DESC
